create table "chat" (chat_platforms_id integer not null, group_id bigint, id bigint generated by default as identity, name varchar(255), primary key (id));
create table "group" (id bigint generated by default as identity, token varchar(64), name varchar(255), primary key (id));
create table "role" (id bigint generated by default as identity, name varchar(255), primary key (id));
create table "user" (id bigint generated by default as identity, email varchar(255), first_name varchar(255), last_name varchar(255), password varchar(255), primary key (id));
create table chat_platform (id integer generated by default as identity, name varchar(100), primary key (id));
create table group_chat_mapping (chat_id bigint not null, group_id bigint not null, primary key (chat_id, group_id));
create table group_announcement (end_time timestamp(6), "group_id" bigint, id bigint generated by default as identity, start_time timestamp(6), content TEXT not null, primary key (id));
create table invitation_token (expiry_date timestamp(6), "group_id" bigint, id bigint generated by default as identity, token varchar(64), primary key (id));
create table message ("chat_id" bigint, id bigint generated by default as identity, timestamp timestamp(6), content TEXT not null, username varchar(255), primary key (id));
create table user_group (group_id bigint not null, user_id bigint not null, primary key (group_id, user_id));
create table user_roles (role_id bigint not null, user_id bigint not null, primary key (role_id, user_id));
create table user_group_role (group_id bigint, id bigint generated by default as identity, role_id bigint, user_id bigint, primary key (id));
